export const GetIpfsUrlFromPinata = (pinataUrl) => {
    var IPFSUrl = pinataUrl.split("/");
    const lastIndex = IPFSUrl.length;
    IPFSUrl = "https://ipfs.io/nipfs/"+IPFSUrl[lastIndex-1];
    return IPFSUrl;
};
